mapper.select:
SELECT USER_ID
FROM   USER
WHERE  USER_ID = #{userId}

mapper.update:
UPDATE USER
SET PASSWORD = #{password}
WHERE USER_ID = #{userId}

UPDATE USER
SET    EMAIL = #{email}
WHERE  USER_ID = #{userId}

UPDATE USER
SET    HP = #{hp}
WHERE  USER_ID = #{userId}

